import React, {Component} from 'react';


export default class CreateTicket extends Component{
    constructor(props){
        super(props);
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangeTicketNumber = this.onChangeTicketNumber.bind(this);
        this.onChangeSiteNumber= this.onChangeSiteNumber.bind(this);
        this.onChangeDescription=this.onChangeDescription.bind(this);
        this.onChangeDate=this.onChangeDate.bind(this);
        this.onSubmit=this.onSubmit.bind(this);

        this.state={
            username:'',
            ticketnumber:0,
            siteNumber:'',
            description:'',
            date:new Date(),
            user:[]
        }
    }

    onChangeUsername(e){
        this.setState({
            username:e.target.value
        })
    }
    onChangeTicketNumber(e){
        this.setState({
            ticketnumber:e.target.value
        })
    }
    onChangeSiteNumber(e){
        this.setState({
            siteNumber:e.target.value
        })
    }
    onChangeDescription(e){
        this.setState({
            description:e.target.value
        })
    }
    onChangeDate(date){
        this.setState({
        date:date
        })
    }
    onSubmit(e){
        e.preventDefault();
        const ticket ={
            username:this.state.username,
            ticketnumber:this.state.ticketnumber,
            siteNumber:this.state.siteNumber,
            description:this.state.description,
            date:this.state.date
        }
        console.log(ticket);

        window.location='/';
    }
    render(){
        return(
            <div>
                <h3> Create Ticket</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Username: </label>
                        <input type="text"
                        required
                        className="form-control"
                        value={this.state.username}
                        onChange={this.onChangeUsername}
                        />
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Create User" className=" btn btn-primary"/>
                    </div>
                </form>
            </div>
        )
    }
}
